public String uploadFile(String imagePath) throws IOException
 {
StrictMode.ThreadPolicy policy = new StrictMode.ThreadPolicy.Builder().permitAll().build();
        StrictMode.setThreadPolicy(policy);
  BufferedReader br = null;

  String fileName = imagePath;
  HttpURLConnection conn = null;
  DataOutputStream dos = null;  
  String lineEnd = "
";
  String twoHyphens = "--";
  String boundary = "*****";
  int bytesRead, bytesAvailable, bufferSize;
  byte[] buffer;
  int maxBufferSize = 1 * 1024 * 1024; 
  File sourceFile = new File(imagePath); 

  if (!sourceFile.isFile())
  {



            System.out.println("uploadFile: Source File not exist :" + imagePath);


  }
  else
  {
            try
   { 


                FileInputStream fileInputStream = new FileInputStream(sourceFile);
                URL url = new URL("http://site.com/upload.php");


                conn = (HttpURLConnection) url.openConnection(); 
                conn.setDoInput(true); 
                conn.setDoOutput(true); 
                conn.setUseCaches(false); 
                conn.setRequestMethod("POST");
                conn.setRequestProperty("Connection", "Keep-Alive");
                conn.setRequestProperty("ENCTYPE", "multipart/form-data");
                conn.setRequestProperty("Content-Type", "multipart/form-data;boundary=" + boundary);
                conn.setRequestProperty("uploaded_file", fileName); 

                dos = new DataOutputStream(conn.getOutputStream());

                dos.writeBytes(twoHyphens + boundary + lineEnd); 
                dos.writeBytes("Content-Disposition: form-data; name="uploaded_file";filename=""
          + fileName + """ + lineEnd);

                dos.writeBytes(lineEnd);


                bytesAvailable = fileInputStream.available(); 

                bufferSize = Math.min(bytesAvailable, maxBufferSize);
                buffer = new byte[bufferSize];



                bytesRead = fileInputStream.read(buffer, 0, bufferSize);  

                while (bytesRead > 0)
    {

     dos.write(buffer, 0, bufferSize);
     bytesAvailable = fileInputStream.available();
     bufferSize = Math.min(bytesAvailable, maxBufferSize);
     bytesRead = fileInputStream.read(buffer, 0, bufferSize);   

    }


                dos.writeBytes(lineEnd);
                dos.writeBytes(twoHyphens + boundary + twoHyphens + lineEnd);


                int serverResponseCode = conn.getResponseCode();
                String serverResponseMessage = conn.getResponseMessage();

                System.out.println("uploadFile : HTTP Response is : " 
           + serverResponseMessage + ": " + serverResponseCode);

    if (200 <= conn.getResponseCode() && conn.getResponseCode() <= 299)
    {
     br = new BufferedReader(new InputStreamReader(conn.getInputStream()));
    }
    else
    {
     br = new BufferedReader(new InputStreamReader(conn.getErrorStream()));
    }


                
                fileInputStream.close();
                dos.flush();
                dos.close();

   }
   catch (MalformedURLException ex)
   {


    ex.printStackTrace();

    System.out.println("Upload file to server: error: " + ex.getMessage());  
   }
   catch (Exception e)
   {


    e.printStackTrace();


   }




  } 
  return br.readLine();
 }

